function showMeTooltip(){var e=window.location.toString();var t="https://twitter.com/intent/tweet?text="+getTextToShare()+"&via="+twitterAccount+"&url="+e;$("#shareTooltip").show();$("#shareTooltip").animate({opacity:1},30);$("#sendToTwitter").attr("href",t)}function getTextToShare(){shareTxt="";if(window.getSelection){shareTxt=window.getSelection();generateTooltipPosition()}else if(document.getSelection){shareTxt=document.getSelection();generateTooltipPosition()}return shareTxt}function generateTooltipPosition(){var e=window.getSelection&&window.getSelection();if(e&&e.rangeCount>0){range=e.getRangeAt(0);pos=$(window).scrollTop();selection_text=e.toString(),rect=range.getBoundingClientRect();$("#shareTooltip").css({top:rect.top+pos-20-32+"px",left:rect.left+rect.width/2+"px"})}}$(document).ready(function(){textToShare="";$(document).mousemove(function(e){generateTooltipPosition()})});$(document).mouseup(function(){$(document).mousemove(function(e){generateTooltipPosition()});var e=getTextToShare();var t=document.getElementById("shareTooltip");if(e!="")showMeTooltip()});$(document).click(function(){var e=getTextToShare();var t=null;var n=$("#shareTooltip").attr("title");if(n=="")return;if(n!==t)$("#shareTooltip").animate({opacity:0},30);if(e!="")showMeTooltip()});$(window).resize(function(){if($("#shareTooltip").show()){$("#shareTooltip").animate({opacity:0},30)}})